import './Todo.css';
import React, { useEffect, useState } from 'react';
import api from '../../api';





function Home() {

  const [projects, setProjects]= useState([]);
  const [formData, setformData]= useState({name:""});


  useEffect(()=>{
    
  })


  const fetchProjects = async () => {
    const token = localStorage.getItem('token');
    if (token) {
        try {
            const response = await api.get('/projects', { headers: { Authorization: `Bearer ${token}` } });
            setProjects(response.data);
        } catch (err) {
            console.error('Error fetching projects:', err);
        }
    }
};



const handleChange = (e) => {
  setFormData({
      ...formData,
      [e.target.name]: e.target.value
  });
};

const handleSubmit = async (e) => {
  e.preventDefault();
  const token = localStorage.getItem('token');
  if (token) {
      try {
          const response = await api.post('/projects', formData, { headers: { Authorization: `Bearer ${token}` } });
          alert(response.data.message);
          setFormData({ name: '' });
          fetchProjects();
      } catch (err) {
          alert(err.response.data.message);
      }
  } else {
      alert('You must be logged in to add a project.');
  }
};



  return (
    <div>
    <h1>Projects</h1>
    <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Project Name" onChange={handleChange} value={formData.name} required />
        <button type="submit">Add Project</button>
    </form>
    <h2>Project List</h2>
    <ul>
        {projects.map((project) => (
            <li key={project._id}>
                <h3>{project.name}</h3>
                <p><em>Created At: {new Date(project.createdAt).toLocaleString()}</em></p>
            </li>
        ))}
    </ul>
</div>
);
};

export default Home;
