import './Home.css';
import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../../api';
import { IoIosDocument } from 'react-icons/io'

const Project = () => {
  const [projects, setProjects] = useState([]);
  const [formData, setFormData] = useState({ name: '', description: '' });
  const [showAddProject, setShowAddProject] = useState(false);
  const [selectedProjectId, setSelectedProjectId] = useState(null);
  const [collaborators, setCollaborators] = useState([{ userId: '', role: '' }]);
  const navigate = useNavigate();


  useEffect(() => {
    fetchProjects();
  }, []);

  const handleAddClick = () => {
    setShowAddProject(true);
  };

  const fetchProjects = async () => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const response = await api.get('/projects', { headers: { Authorization: `Bearer ${token}` } });
        console.log('Fetched projects:', response); // Debug log
        setProjects(response.data);
      } catch (err) {
        console.error('Error fetching projects:', err);
        alert('Error fetching projects. Please try again later.');
      }
    } else {
      alert('You must be logged in to view projects.');
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };



  const handleCollaboratorChange = (index, e) => {
    const newCollaborators = [...collaborators];
    newCollaborators[index][e.target.name] = e.target.value;
    setCollaborators(newCollaborators);
  };

  const addCollaborator = () => {
    setCollaborators([...collaborators, { userId: '', role: '' }]);
  };

  const removeCollaborator = (index) => {
    const newCollaborators = collaborators.filter((_, i) => i !== index);
    setCollaborators(newCollaborators);
  };





  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decodedToken = decodeToken(token);
        const userId = decodedToken.userId;

        const response = await api.post('/projects', { ...formData, userId, collaborators }, { headers: { Authorization: `Bearer ${token}` } });
        alert('Project added successfully!');
        setFormData({ name: '', description: '' });
        setCollaborators([{ userId: '', role: '' }]); // Reset collaborators

        fetchProjects();

        // Store the new project ID in localStorage
        // const newProject = response.data;
        // localStorage.setItem('selectedProject', JSON.stringify(newProject));
      } catch (err) {
        console.error('Error adding project:', err);
        alert('Error adding project. Please check your input and try again.');
      }
    } else {
      alert('You must be logged in to add a project.');
    }
  };



  // Function to decode the JWT token and extract userId
  const decodeToken = (token) => {
    // Ensure you handle token parsing securely
    const payload = token.split('.')[1];
    const decodedPayload = atob(payload);
    return JSON.parse(decodedPayload);
  };



  const handleSelectProject = (projectId) => {
    setSelectedProjectId(projectId);
    localStorage.setItem('selectedProject', projectId); // Store as string
  };


  const handleAddCollaborator = async (projectId, username, role) => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const response = await api.put('/projects/collaborators', { projectId, username, role }, { headers: { Authorization: `Bearer ${token}` } });
        alert('Collaborator added successfully!');
        fetchProjects();
      } catch (err) {
        console.error('Error adding collaborator:', err);
        alert('Error adding collaborator. Please check your input and try again.');
      }
    } else {
      alert('You must be logged in to add a collaborator.');
    }
  };




  return (

    <div className="project-container">
    <h1>Projects</h1>
    <button onClick={handleAddClick}>Add Project</button>
    {showAddProject && (
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Description:</label>
          <textarea name="description" value={formData.description} onChange={handleChange} required />
        </div>
        <h3>Collaborators</h3>
        {collaborators.map((collaborator, index) => (
          <div key={index}>
            <label>Username:</label>
            <input type="text" name="username" value={collaborator.username} onChange={(e) => handleCollaboratorChange(index, e)} required />
            <label>Role:</label>
            <input type="text" name="role" value={collaborator.role} onChange={(e) => handleCollaboratorChange(index, e)} required />
            {collaborators.length > 1 && (
              <button type="button" onClick={() => removeCollaborator(index)}>Remove</button>
            )}
          </div>
        ))}
        <button type="button" onClick={addCollaborator}>Add Collaborator</button>
        <button type="submit">Submit</button>
      </form>
    )}
    <ul>
      {projects.map((project) => (
        <li key={project._id}>
          <Link to={`/projects/document/${project._id}`}>
            <IoIosDocument size={24} /> {project.name}
          </Link>
          <button onClick={() => setSelectedProjectId(project._id)}>Add Collaborator</button>
          {selectedProjectId === project._id && (
            <form onSubmit={(e) => {
              e.preventDefault();
              const username = e.target.elements.username.value;
              const role = e.target.elements.role.value;
              handleAddCollaborator(project._id, username, role);
            }}>
              <div>
                <label>Username:</label>
                <input type="text" name="username" required />
              </div>
              <div>
                <label>Role:</label>
                <input type="text" name="role" required />
              </div>
              <button type="submit">Add Collaborator</button>
            </form>
          )}
        </li>
      ))}
    </ul>
  </div>
);
};

export default Project;
