import React, { useState, useEffect } from 'react';
import './document.css'; 
import api from '../../api'; 

const Document = () => {
  const [files, setFiles] = useState([]);
  const [projectId, setProjectId] = useState(''); // This should be set when a project is selected
  const [selectedProject, setSelectedProject] = useState(null);


  useEffect(()=>{
    const storedProject = localStorage.getItem('selectedProject')
    if(storedProject){
        setSelectedProject(JSON.parse(storedProject))
    }
    else{
        alert("No project selected")
    }
  },[]);

  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    setFiles(selectedFiles);
  };

  const handleFileUpload = async (event) => {
    event.preventDefault();
    if (!selectedProject) {
      alert('No project selected');
      return;
    }

    const formData = new FormData();
    formData.append('projectId', selectedProject._id);
    files.forEach(file => {
      formData.append('files', file);
    });

    try {
      const token = localStorage.getItem('token');
      const response = await api.post('/projects/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`,
        },
      });
      console.log('Files uploaded successfully:', response.data);
      alert('Files uploaded successfully!');
      setFiles([]); // Clear the file input after successful upload
    } catch (error) {
      console.error('Error uploading files:', error);
      alert('Error uploading files. Please try again.');
    }
  };


  return (
    <div className="folder-box-container">
    <h2>Upload Files</h2>
    {selectedProject ? (
      <div className="folder-box">
        <input
          type="file"
          multiple
          onChange={handleFileChange}
          className="file-input"
        />
        <button onClick={handleFileUpload} className="upload-button">
          Upload Files
        </button>
        {files.length > 0 && (
          <div className="file-box">
            <h3>Uploaded Files:</h3>
            <ul>
              {files.map((file, index) => (
                <li key={index}>{file.name}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    ) : (
      <p>No project selected</p>
    )}
  </div>
);
};

export default Document;
