import React, { useState } from 'react';
import './document.css'; // Import your CSS file for styling

const Document = () => {
  const [files, setFiles] = useState([]);

  const handleFileChange = (event) => {
    const newFiles = Array.from(event.target.files);
    setFiles(prevFiles => [...prevFiles, ...newFiles]);
  };

  const handleFileUpload = (event) => {
    event.preventDefault();
    // Handle file upload logic here (e.g., send files to a server)
    console.log('Files ready for upload:', files);
    setFiles([]); // Clear the files after upload if needed
  };

  return (
    <div className="folder-box-container">
    <h2>Upload Files</h2>
    <div className="folder-box">
      <input
        type="file"
        multiple
        onChange={handleFileChange}
        className="file-input"
      />
      <button onClick={handleFileUpload} className="upload-button">
        Upload Files
      </button>
      {files.length > 0 && (
        <div className="file-box">
          <h3>Selected Files:</h3>
          <ul>
            {files.map((file, index) => (
              <li className='file-list' key={index}>{file.name}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  </div>
);
};
export default Document;
