import './Home.css';
import React, { useEffect, useState } from 'react';
import api from '../../api';

const Project = () => {
  const [projects, setProjects] = useState([]);
  const [formData, setFormData] = useState({ name: '', description: '' });

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const response = await api.get('/projects', { headers: { Authorization: `Bearer ${token}` } });
        setProjects(response.data);
      } catch (err) {
        console.error('Error fetching projects:', err);
      }
    } else {
      alert('You must be logged in to view projects.');
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const userId = decodeToken(token).userId; // Adjust as necessary
        const response = await api.post('/projects', { ...formData, userId }, { headers: { Authorization: `Bearer ${token}` } });
        alert('Project added successfully!');
        setFormData({ name: '', description: '' });
        fetchProjects(); // Ensure this is called
      } catch (err) {
        alert(err.response ? err.response.data.message : 'Error adding project');
      }
    } else {
      alert('You must be logged in to add a project.');
    }
  };
  
  // Function to decode the JWT token and extract userId
  const decodeToken = (token) => {
    const payload = token.split('.')[1];
    const decodedPayload = atob(payload);
    return JSON.parse(decodedPayload);
  };

  return (
    <div>
      <h1>Projects</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Project Name"
          onChange={handleChange}
          value={formData.name}
          required
        />
        <textarea
          name="description"
          placeholder="Project Description"
          onChange={handleChange}
          value={formData.description}
        />
        <button type="submit">Add Project</button>
      </form>
      <h2>Project List</h2>
    <ul>
      {projects.length > 0 ? (
        projects.map((project) => (
          <li key={project._id}>
            <h3>{project.name}</h3>
            <p>{project.description}</p>
          </li>
        ))
      ) : (
        <p>No projects found</p>
      )}
    </ul>
  </div>
);
}

export default Project;
