import './Home.css';
import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../../api';
import { IoIosDocument } from 'react-icons/io'

const Project = () => {
  const [projects, setProjects] = useState([]);
  const [formData, setFormData] = useState({ name: '', description: '' });
  const [showAddProject, setShowAddProject] = useState(false);
  const [selectedProjectId, setSelectedProjectId] = useState(null);
  const [collaborators, setCollaborators] = useState([{ userId: '', role: '' }]);
  const navigate = useNavigate();


  useEffect(() => {
    fetchProjects();
  }, []);

  const handleAddClick = () => {
    setShowAddProject(true);
  };

  const fetchProjects = async () => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const response = await api.get('/projects', { headers: { Authorization: `Bearer ${token}` } });
        console.log('Fetched projects:', response); // Debug log
        setProjects(response.data);
      } catch (err) {
        console.error('Error fetching projects:', err);
        alert('Error fetching projects. Please try again later.');
      }
    } else {
      alert('You must be logged in to view projects.');
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };



  const handleCollaboratorChange = (index, e) => {
    const newCollaborators = [...collaborators];
    newCollaborators[index][e.target.name] = e.target.value;
    setCollaborators(newCollaborators);
  };

  const addCollaborator = () => {
    setCollaborators([...collaborators, { userId: '', role: '' }]);
  };

  const removeCollaborator = (index) => {
    const newCollaborators = collaborators.filter((_, i) => i !== index);
    setCollaborators(newCollaborators);
  };





  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decodedToken = decodeToken(token);
        const userId = decodedToken.userId;

        const response = await api.post('/projects', { ...formData, userId, collaborators  }, { headers: { Authorization: `Bearer ${token}` } });
        alert('Project added successfully!');
        setFormData({ name: '', description: '' });
        setCollaborators([{ userId: '', role: '' }]); // Reset collaborators

        fetchProjects();

        // Store the new project ID in localStorage
        // const newProject = response.data;
        // localStorage.setItem('selectedProject', JSON.stringify(newProject));
      } catch (err) {
        console.error('Error adding project:', err);
        alert('Error adding project. Please check your input and try again.');
      }
    } else {
      alert('You must be logged in to add a project.');
    }
  };



  // Function to decode the JWT token and extract userId
  const decodeToken = (token) => {
    // Ensure you handle token parsing securely
    const payload = token.split('.')[1];
    const decodedPayload = atob(payload);
    return JSON.parse(decodedPayload);
  };



  const handleSelectProject = (projectId) => {
    setSelectedProjectId(projectId);
    localStorage.setItem('selectedProject', projectId); // Store as string
  };






  return (

    <div className='main'>

      <Link to="/projects/document"><IoIosDocument className='doc-icon' /></Link>

      <div className="projects-container">
        <div className="projects-header">
          <h1>Projects</h1>
          <button className="add-project-button" onClick={handleAddClick}>
            Add Project
          </button>
        </div>
        {showAddProject && (
          <div className="projects-form-container">
            <form onSubmit={handleSubmit} className="project-form">
              <input
                type="text"
                name="name"
                placeholder="Project Name"
                onChange={handleChange}
                value={formData.name}
                required
              />
              <textarea
                name="description"
                placeholder="Project Description"
                onChange={handleChange}
                value={formData.description}
              />

              <div className="collaborators-section">
                <h3>Collaborators</h3>
                {collaborators.map((collaborator, index) => (
                  <div key={index} className="collaborator-form">
                    <input
                      type="text"
                      name="userId"
                      placeholder="Collaborator User ID"
                      onChange={(e) => handleCollaboratorChange(index, e)}
                      value={collaborator.userId}
                      required
                    />
                    <input
                      type="text"
                      name="role"
                      placeholder="Role (e.g., editor, viewer)"
                      onChange={(e) => handleCollaboratorChange(index, e)}
                      value={collaborator.role}
                      required
                    />
                    <button type="button" onClick={() => removeCollaborator(index)}>Remove</button>
                  </div>
                ))}
                <button type="button" onClick={addCollaborator}>Add Collaborator</button>
              </div>


              <div className="form-buttons">
                <button type="submit">Create</button>
                <button type="button" onClick={() => setShowAddProject(false)}>Cancel</button>
              </div>
            </form>
          </div>
        )}
        <div className="project-list-container">
          <h2>Project List</h2>
          <ul>
            {projects.length > 0 ? (
              projects.map((project) => (
                <li key={project._id}
                  className={`project-item ${selectedProjectId === project._id ? 'selected' : ''}`}
                  onClick={() => handleSelectProject(project._id)}>
                  <h3>{project.name}, {project._id}</h3>
                  <p>{project.description}</p>

                </li>
              ))
            ) : (
              <p>No projects found</p>
            )}
          </ul>
        </div>
      </div>
    </div>

  );
};

export default Project;
